import React from 'react';
import { useState } from 'react';
import buttonCreate from '../css/buttonCreate.module.css';
import pokeButton from '../images/open-pokeball.png';
import {handleErrors} from '../redux/actions/index';




const ButtonCreate = () => {

    const [errors, setErrors] = useState({
        name: '',
        life: '',
        defense: '',
        atack: '',
        speed: '',
        weight: '',
        height: '',
        type: [],
        id: stateRedux.pokeModify.id,

    });

    // creamos una vallidacon para que en la base de datos no se repitan los nombres de los pokemones y muestre un error
     const handleErrors = (input) => {
        useState = {
            errors: {
                name: '',

            }
        }
        let errors = {
            name: "",
            hp: "",
            attack: "",
            defense: "",
            speed: "",
            height: "",
            weight: "",
            type: [],
        id: stateRedux.pokeModify.id,

        };
        if (!input.name) {
            errors.name = "Name is required";
        } else if (!/^[A-Za-z]+$/.test(input.name)) {
            errors.name = "Name must be only letters";
        }
        return errors;
    };




    return (

        <button type="submit" className={buttonCreate.button}>
            <img className={buttonCreate.poke1} src={pokeButton} alt="Img not found" />
         {/* aca va el boton de validate */}
           <b onClick={handleErrors}>crear</b>
            <img className={buttonCreate.poke2} src={pokeButton} alt="Img not found" />
        </button>
    )
};

export default ButtonCreate;
