import React from 'react'
import loading from './loading.module.css'
import pikachuLoad from '../images/loading.gif'


const Loading = () => {
//  vamos hacer una validacon si hay un pokemon que muester un mensaje de error y si hay 

  $validacion = () => {
    if (this.props.pokemon === null) {
        return <p> No hay pokemon </p>
    } else {
        return <img src={pikachuLoad} alt="loading" className={loading.loading} />
    }
    }
    return (
        <div>
            {this.$validacion()}

        </div>
    )
}




